create table usuarios(
  id serial primary key,
	nome text (250) not null,
  telefone int,
  email text (250) not null,
  senha text(250) not null,
  cidade text(250),
  cor text(100),
  possui_deficiencia boolean not null,
  deficiencia text,
  cpf text(11),
  cep text (8),
  data_nascimento date,
  genero text(100),
  assinante boolean default false,
  foto_perfil text
);

create table parceiros(
	id serial primary key,
  nome text(250) not null,
  cnpj text(50) not null
);

create table redatores(
	id serial primary key,
  nome text (250),
  email text(250),
  salario int
);

create table artigos(
	id serial primary key,
  id_redator int references redatores(id),
  titulo text (150) not null,
  conteudo text not null,
  imagens text [],
  tags text [],
  data_artigo date default now()
);


  
  
  
  
  
ALTER TABLE artigos 
DROP COLUMN tags;

ALTER TABLE artigos 
ADD COLUMN tags text;
  
  


select  *  from artigos where tags ilike '%bootcamp%';

select count ( * ) from artigos where id_redator = 3;

select  *  from usuarios where assinante = true;

select nome as redator, titulo as materia, data_artigo as publicado_em 
from redatores 
join artigos 
on id(redatores) = id_redator(artigos);

select nome as redator, count ( * ) as qtd_artigos 
from artigos 
join redatores 
on id(redatores) = id_redator(artigos) 
group by nome;